import React, { useContext,useState } from 'react';
import {TransactionContext} from './TransContext';


function Child(){
   let {transactions, addTransaction} =useContext(TransactionContext);
   let [newDesc,setDesc]=useState("");
   let [newAmount,setAmount]=useState("");

   const handleAddition=(event)=>{
       event.preventDefault();
       console.log(newDesc, newAmount);
       addTransaction({
           amount: newAmount,
           desc:newDesc
       })
   }
   
    return(
        <div className="container">
            <h1 className="text-center">
                Expense Tracker 

            </h1>
            <h3>Your Balance <br/> $260  </h3>
              <div className="expense-container">
                 <h3>INCOME <hr/> $500 </h3>
                  <h3>EXPENSE <hr/> $240  </h3>    
              </div>
              <h3>
                  History
              </h3>
              <hr/>
              <ul className="transection-list">
                  {transactions.map((transObj, ind)=>{
                      return( <li   key={ind}>
                        <span>{transObj.desc}</span>
                        <span>{transObj.amount}</span>
  
                    </li>)
                  })}
              </ul>


              <h3>Add new Transaction</h3>
              <hr/>
              <form className="transection-form" onSubmit={handleAddition}>
                  <label>
                      Enter Discription <br/>
                      <input type="text"  onChange={(ev)=>setDesc(ev.target.value)} required/>

                  </label>
                  <hr/>
                  <label>
                      Enter Amount <br/>
                      <input type='number' onChange={(ev)=>setAmount(ev.target.value)} required/>
                  </label>
                  <hr/>
                  <input type="submit" value="add Transection" />


              </form>
               
              
            
        </div>

    )
}
export default Child;
