import React,{useContext, useState} from 'react'
import {TransactionContext} from './TransContext'


function Child(){

  let {transactions,addTransaction} = useContext(TransactionContext);
  let [newDesc , setDesc] = useState("");
  let [newAmount , setAmount] = useState("");

 
  const handleAddition=(event)=>{
 event.preventDefault(); //to stop reloading effect
 console.log(newDesc,newAmount);
 addTransaction({

    amount:newAmount,
    desc:newDesc
  
    })

  }

return (

    <div className="container"> 
    <h1 className="text-center">Expense Tracker</h1>
    <h3> Your Balance <hr/> $260</h3>
          <div className="expense-container">
                <h3> Income <hr/> $260</h3>

                 <h3> Expense <hr/>$260</h3>

         </div>
         <h3> History</h3> <hr/>
         <ul className="transection-list">
         {transactions.map((transObj, ind)=>{
         return(<li key={ind}>
                <span>{transObj.desc}</span>
                <span>{transObj.amount}</span>
                </li>)

         })}

       
        
         
         
         
         </ul>
         <h3> Add New Transection</h3> <hr/>

    <form className="transection-form" onSubmit={handleAddition}>

    <label>
    Enter Description <hr/>
    <input type="text" required onChange={(ev)=>setDesc(ev.target.value)}/>
 
    
    </label>

    <label>
    <br/>
    Enter Amount <hr/> 
    <input type="number" required onChange={(ev)=>setAmount(ev.target.value)}/> <hr/>
    <input type="submit" value ="Add Transection" />
    
    </label>
    
    
    </form>
    </div>
)


}
export default Child;
